import DelFrame from "@/components/DelFrame.vue";
import DataViewSidebar from "./DataViewSidebar.vue";
export default {
  components: {
    DataViewSidebar: DataViewSidebar,
    DelFrame: DelFrame
  },
  data: function data() {
    return {
      addNewDataSidebar: false,
      sidebarData: {},
      users: [],
      //删除------ + isDel方法
      delValide: false,
      delStatus: 0,
      del_id: '' //----------

    };
  },
  computed: {},
  methods: {
    getList: function getList() {
      var _this = this;

      _this.$axios({
        url: '/admin/Recharge/getPayList',
        method: "post",
        data: {
          page: _this.currentPage,
          pageSize: _this.pageSize,
          type: 'alipay'
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          _this.users = res.data.data.list;
        } else {
          _this.users = [];
        }
      });
    },
    //删除
    remove: function remove(id) {
      var _this = this;

      _this.del_id = id;

      _this.closeDel();
    },
    closeDel: function closeDel() {
      var _this = this;

      _this.delValide = true;
    },
    isDel: function isDel() {
      var val = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : false;

      var _this = this;

      if (val) {
        _this.$axios({
          url: "/admin/Recharge/delPayInfo",
          method: "post",
          data: {
            id: _this.del_id
          }
        }).then(function (res) {
          if (res.data.status == 1) {
            _this.getList();

            _this.delStatus = 1;
          } else {
            _this.delStatus = 2;
          }
        });
      }

      _this.delValide = false;
    },
    addNewData: function addNewData() {
      this.sidebarData = {};
      this.toggleDataSidebar(true);
    },
    editNewData: function editNewData(val) {
      this.sidebarData = val;
      this.toggleDataSidebar(true);
    },
    toggleDataSidebar: function toggleDataSidebar() {
      var val = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : false;
      this.addNewDataSidebar = val;
      this.getList();
    },
    change: function change(tr) {
      var _this2 = this;

      var _this = this;

      _this.$axios({
        url: '/admin/Recharge/editStatus',
        method: "post",
        data: {
          id: tr.id,
          type: 'alipay',
          status: tr.status
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          _this2.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'success'
          });
        } else {
          _this2.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'danger'
          });
        }

        _this.getList();
      });
    }
  },
  mounted: function mounted() {
    this.getList();
  }
};
