import { AgGridVue } from 'ag-grid-vue';
import '@/assets/scss/vuexy/extraComponents/agGridStyleOverride.scss';
import vSelect from 'vue-select'; // Store Module

import moduleUserManagement from '@/store/user-management/moduleUserManagement.js'; // Cell Renderer
// import CellRendererLink from '../user-list/cell-renderer/CellRendererLink.vue'
// import CellRendererStatus from '../user-list/cell-renderer/CellRendererStatus.vue'
// import CellRendererVerified from '../user-list/cell-renderer/CellRendererVerified.vue'
// import CellRendererActions from '../user-list/cell-renderer/CellRendererActions.vue'

export default {
  components: {
    AgGridVue: AgGridVue,
    vSelect: vSelect
  },
  props: {
    data: {
      type: Object,
      required: true
    }
  },
  data: function data() {
    return {
      searchQuery: '',
      // AgGrid
      list: [{
        id: "200",
        username: "微信",
        email: "26032626xxaaw22",
        name: "2020-12-22"
      }],
      gridApi: null,
      gridOptions: {},
      defaultColDef: {
        sortable: true,
        resizable: true,
        suppressMenu: true
      },
      columnDefs: [{
        headerName: '充值金额',
        field: 'money',
        width: 125,
        filter: true
      }, {
        headerName: '支付方式',
        field: 'mode',
        filter: true,
        width: 210
      }, {
        headerName: '订单号',
        field: 'order_num',
        filter: true,
        width: 225
      }, {
        headerName: '充值时间',
        field: 'create_time',
        filter: true,
        width: 200
      }, {
        headerName: '状态',
        field: 'statusStr',
        filter: true,
        width: 200
      }],
      components: {},
      currentPage: 1,
      pageSize: 10,
      totalPages: 0,
      total: 0,
      recharge: ''
    };
  },
  watch: {
    roleFilter: function roleFilter(obj) {
      this.setColumnFilter('role', obj.value);
    },
    statusFilter: function statusFilter(obj) {
      this.setColumnFilter('status', obj.value);
    },
    isVerifiedFilter: function isVerifiedFilter(obj) {
      var val = obj.value === 'all' ? 'all' : obj.value === 'yes' ? 'true' : 'false';
      this.setColumnFilter('is_verified', val);
    },
    departmentFilter: function departmentFilter(obj) {
      this.setColumnFilter('department', obj.value);
    },
    pageSize: function pageSize() {
      this.getRechargeList();
    },
    currentPage: function currentPage() {
      this.getRechargeList();
    }
  },
  computed: {},
  methods: {
    getRechargeList: function getRechargeList() {
      var _this = this;

      _this.$axios({
        url: '/admin/User/rechargeList',
        method: "post",
        data: {
          page: _this.currentPage,
          pageSize: _this.pageSize,
          player_id: _this.player_id
        }
      }).then(function (res) {
        console.log(res);

        if (res.data.status == 1) {
          _this.list = res.data.data.list;
          _this.total = res.data.data.total;
          _this.totalPages = _this.pageTotal(_this.total, _this.pageSize);
        } else {
          _this.list = [];
          _this.total = 0;
          _this.totalPages = 1;
        }
      });
    },
    input: function input(val) {
      console.log(val);
    },
    recharge_btn: function recharge_btn() {
      var _this = this;

      this.$vs.dialog({
        type: 'confirm',
        // color: 'danger',
        title: '提示',
        text: "\u786E\u5B9A\u7ED9\u8BE5\u7528\u6237\u5145\u503C " + _this.recharge + " \u94BB\uFF1F",
        accept: _this.sure,
        cancel: _this.cancel,
        acceptText: '确定',
        cancelText: '取消'
      });
    },
    sure: function sure() {
      var _this = this;

      _this.$axios({
        url: '/admin/User/recharge',
        method: "post",
        data: {
          recharge: _this.recharge,
          player_id: _this.player_id
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          _this.getRechargeList();

          _this.recharge = '';

          _this.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'success'
          });
        } else {
          _this.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'danger'
          });
        }
      });
    },
    cancel: function cancel() {
      this.continue = false;
    },
    //"总条数"：rowCount，"每页总条数"：pageSize
    pageTotal: function pageTotal(rowCount, pageSize) {
      if (rowCount == null || rowCount == "") {
        return 0;
      } else {
        if (pageSize != 0 && rowCount % pageSize == 0) {
          return parseInt(rowCount / pageSize);
        }

        if (pageSize != 0 && rowCount % pageSize != 0) {
          return parseInt(rowCount / pageSize) + 1;
        }
      }
    },
    setColumnFilter: function setColumnFilter(column, val) {
      var filter = this.gridApi.getFilterInstance(column);
      var modelObj = null;

      if (val !== 'all') {
        modelObj = {
          type: 'equals',
          filter: val
        };
      }

      filter.setModel(modelObj);
      this.gridApi.onFilterChanged();
    },
    resetColFilters: function resetColFilters() {
      // Reset Grid Filter
      this.gridApi.setFilterModel(null);
      this.gridApi.onFilterChanged(); // Reset Filter Options

      this.roleFilter = this.statusFilter = this.isVerifiedFilter = this.departmentFilter = {
        label: 'All',
        value: 'all'
      };
      this.$refs.filterCard.removeRefreshAnimation();
    },
    updateSearchQuery: function updateSearchQuery(val) {
      this.gridApi.setQuickFilter(val);
    }
  },
  mounted: function mounted() {
    this.gridApi = this.gridOptions.api;
    this.player_id = this.data.id;
    this.getRechargeList(); // if (this.$vs.rtl) {
    //   const header = this.$refs.agGridTable.$el.querySelector('.ag-header-container')
    //   header.style.left = `-${  String(Number(header.style.transform.slice(11, -3)) + 9)  }px`
    // }
  },
  created: function created() {// if (!moduleUserManagement.isRegistered) {
    //   this.$store.registerModule('userManagement', moduleUserManagement)
    //   moduleUserManagement.isRegistered = true
    // }
    // this.$store.dispatch('userManagement/fetchUsers').catch(err => { console.error(err) })
  }
};
