import EditerChargeBillie from "./EditerChargeBillie.vue"; // import DelFrame from "@/components/DelFrame.vue"

export default {
  components: {
    EditerChargeBillie: EditerChargeBillie // DelFrame

  },
  data: function data() {
    return {
      addNewDataSidebar: false,
      sidebarData: {},
      set: [],
      total: 1,
      totalPages: 0,
      currentPage: 1,
      pageSize: 10,
      //删除------ + isDel方法
      delValide: false,
      delStatus: 0,
      del_id: '',
      //----------
      exchange_rate: 0
    };
  },
  computed: {},
  watch: {},
  methods: {
    getExchangeRate: function getExchangeRate() {
      var _this = this;

      _this.$axios({
        url: "/admin/Setting/getExchangeRate",
        method: "post",
        data: {
          page: _this.currentPage,
          pageSize: _this.pageSize
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          _this.set = res.data.data.list;
          _this.total = res.data.data.total;
          _this.totalPages = _this.pageTotal(_this.total, _this.pageSize);
        } else {
          _this.set = [];
          _this.total = 0;
        }
      });
    },
    //"总条数"：rowCount，"每页总条数"：pageSize
    pageTotal: function pageTotal(rowCount, pageSize) {
      if (rowCount == null || rowCount == "") {
        return 0;
      } else {
        if (pageSize != 0 && rowCount % pageSize == 0) {
          return parseInt(rowCount / pageSize);
        }

        if (pageSize != 0 && rowCount % pageSize != 0) {
          return parseInt(rowCount / pageSize) + 1;
        }
      }
    },
    toEdit: function toEdit(row) {
      // console.log(row);
      this.sidebarData = row;
      this.toggleDataSidebar(true);
    },
    //添加分组
    addNewData: function addNewData() {
      this.sidebarData = {};
      this.toggleDataSidebar(true);
    },
    toggleDataSidebar: function toggleDataSidebar() {
      var val = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : false;
      this.addNewDataSidebar = val;
    }
  },
  mounted: function mounted() {
    this.getExchangeRate();
  }
};
