import "core-js/modules/es6.number.constructor";
import { AgGridVue } from 'ag-grid-vue';
import '@/assets/scss/vuexy/extraComponents/agGridStyleOverride.scss';
import vSelect from 'vue-select'; // Store Module

import moduleUserManagement from '@/store/user-management/moduleUserManagement.js'; // Cell Renderer

import CellRendererLink from "./box-list/cell-renderer/CellRendererLink.vue";
import CellRendererStatus from "./box-list/cell-renderer/CellRendererStatus.vue";
import CellRendererVerified from "./box-list/cell-renderer/CellRendererVerified.vue";
import CellRendererActions from "./box-list/cell-renderer/CellRendererActions.vue";
import CellRenderImg from "./box-list/cell-renderer/CellRenderImg.vue";
import DataSiderSkin from "./DataSiderSkin.vue";
import AddSkinSider from "./AddSkinSider.vue";
import CellStock from "./box-list/cell-renderer/CellStock.vue";
import CellStockVip from "./box-list/cell-renderer/CellStockVip.vue";
export default {
  components: {
    AgGridVue: AgGridVue,
    vSelect: vSelect,
    CellRendererLink: CellRendererLink,
    CellRendererStatus: CellRendererStatus,
    CellRendererVerified: CellRendererVerified,
    CellRendererActions: CellRendererActions,
    CellRenderImg: CellRenderImg,
    DataSiderSkin: DataSiderSkin,
    AddSkinSider: AddSkinSider,
    CellStock: CellStock,
    CellStockVip: CellStockVip
  },
  props: {
    totalProbability: Number
  },
  data: function data() {
    return {
      total: 0,
      totalPages: 0,
      currentPage: 1,
      addNewDataSidebar: false,
      addSkinSidebar: false,
      sidebarData: {},
      searchQuery: '',
      // AgGrid
      list: [],
      skinTypeList: [],
      gridApi: null,
      gridOptions: {},
      defaultColDef: {
        sortable: true,
        resizable: true,
        suppressMenu: true
      },
      columnDefs: [// {
      //   headerName: 'ID',
      //   field: 'id',
      //   width: 125,
      //   filter: true,
      // },
      {
        headerName: '名称',
        field: 'itemName',
        filter: true
      }, //  {
      //   headerName: '图片',
      //   // field: 'imageUrl',
      //   filter: true,
      //   width: 210,
      //   cellRendererFramework: 'CellRenderImg'
      // },
      {
        headerName: '价格',
        field: 'price',
        filter: true,
        width: 120
      }, {
        headerName: '饰品品质',
        field: 'exteriorName',
        filter: true,
        width: 150
      }, {
        headerName: '几率',
        field: 'probability',
        filter: true,
        width: 80
      }, {
        headerName: '库存',
        filter: true,
        width: 135,
        cellRendererFramework: 'CellStock'
      }, //  {
      //   headerName: '初始库存',
      //   field: 'stock',
      //   filter: true,
      //   width: 120
      // },
      {
        headerName: '库存(概率组)',
        field: 'stock_group',
        filter: true,
        width: 130
      }, //  {
      //   headerName: '初始库存(概率组)',
      //   field: 'stock_group',
      //   filter: true,
      //   width: 120
      // },
      {
        headerName: '库存(vip)',
        field: 'stock_vip',
        filter: true,
        width: 135,
        cellRendererFramework: 'CellStockVip'
      }, {
        headerName: '分类',
        field: 'plat_type_name',
        filter: true,
        width: 100
      }, {
        headerName: '添加时间',
        field: 'create_time',
        filter: true // width: 200

      }, {
        headerName: '操作',
        field: 'rem',
        filter: true,
        width: 150,
        cellRendererFramework: 'CellRendererActions'
      }],
      // Cell Renderer Components
      components: {
        CellRendererLink: CellRendererLink,
        CellRendererStatus: CellRendererStatus,
        CellRendererVerified: CellRendererVerified,
        CellRendererActions: CellRendererActions,
        CellStock: CellStock,
        CellStockVip: CellStockVip
      },
      box_id: this.$route.params.userId
    };
  },
  watch: {
    roleFilter: function roleFilter(obj) {
      this.setColumnFilter('role', obj.value);
    },
    statusFilter: function statusFilter(obj) {
      this.setColumnFilter('status', obj.value);
    },
    isVerifiedFilter: function isVerifiedFilter(obj) {
      var val = obj.value === 'all' ? 'all' : obj.value === 'yes' ? 'true' : 'false';
      this.setColumnFilter('is_verified', val);
    },
    departmentFilter: function departmentFilter(obj) {
      this.setColumnFilter('department', obj.value);
    },
    "currentPage": {
      handler: function handler(val) {
        var _this = this; // console.log(val);


        _this.currentPage = val;

        _this.getBoxSkins();
      },
      deep: true
    },
    searchQuery: function searchQuery(val) {
      if (!val) {
        this.getBoxSkins();
      }
    }
  },
  computed: {
    usersData: function usersData() {// console.log(JSON.stringify(this.$store.state.userManagement.users) );
      // return this.$store.state.userManagement.users
    },
    paginationPageSize: function paginationPageSize() {
      if (this.gridApi) return this.gridApi.paginationGetPageSize();else return 10;
    } // totalPages () {
    //   if (this.gridApi) return this.gridApi.paginationGetTotalPages()
    //   else return 0
    // },
    // currentPage: {
    //   get () {
    //     if (this.gridApi) return this.gridApi.paginationGetCurrentPage() + 1
    //     else return 1
    //   },
    //   set (val) {
    //     this.gridApi.paginationGoToPage(val - 1)
    //   }
    // }

  },
  methods: {
    getBoxSkins: function getBoxSkins() {
      var _this = this;

      _this.$axios({
        url: "/admin/Box/boxSkinList",
        method: "post",
        data: {
          box_id: _this.box_id,
          page: _this.currentPage,
          pageSize: _this.paginationPageSize,
          searchKey: _this.searchQuery
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          _this.list = res.data.data.list;
          _this.total = res.data.data.total;
          _this.totalPages = _this.pageTotal(_this.total, _this.paginationPageSize);
        } else {
          _this.list = [];
          _this.total = 0;
          _this.totalPages = 1;
        } // console.log(_this.pageTotal(_this.total,_this.paginationPageSize));

      });
    },
    mendStock: function mendStock() {
      var _this = this;

      _this.$axios({
        url: "/admin/Box/mendStock",
        method: "post",
        data: {
          box_id: _this.box_id
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          _this.getBoxSkins();
        } // console.log(_this.pageTotal(_this.total,_this.paginationPageSize));

      });
    },
    //"总条数"：rowCount，"每页总条数"：pageSize
    pageTotal: function pageTotal(rowCount, pageSize) {
      if (rowCount == null || rowCount == "") {
        return 0;
      } else {
        if (pageSize != 0 && rowCount % pageSize == 0) {
          return parseInt(rowCount / pageSize);
        }

        if (pageSize != 0 && rowCount % pageSize != 0) {
          return parseInt(rowCount / pageSize) + 1;
        }
      }
    },
    getSkinType: function getSkinType() {
      var _this = this;

      _this.$axios({
        url: "/admin/Box/skinType",
        method: "post",
        data: {}
      }).then(function (res) {
        _this.skinTypeList = res.data.data;
      });
    },
    editSkin: function editSkin(pr) {
      // console.log(pr.data);
      this.sidebarData = pr.data;
      this.toggleDataSidebar(true);
    },
    addSkin: function addSkin() {
      this.sidebarData = {};
      this.sidebarData.box_id = this.box_id;
      this.toggleAddSkinSidebar(true);
    },
    reload: function reload() {
      this.$emit("reload");
    },
    toggleDataSidebar: function toggleDataSidebar() {
      var val = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : false;
      this.addNewDataSidebar = val;
    },
    toggleAddSkinSidebar: function toggleAddSkinSidebar() {
      var val = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : false;
      this.addSkinSidebar = val;
    },
    setColumnFilter: function setColumnFilter(column, val) {
      var filter = this.gridApi.getFilterInstance(column);
      var modelObj = null;

      if (val !== 'all') {
        modelObj = {
          type: 'equals',
          filter: val
        };
      }

      filter.setModel(modelObj);
      this.gridApi.onFilterChanged();
    },
    resetColFilters: function resetColFilters() {
      // Reset Grid Filter
      this.gridApi.setFilterModel(null);
      this.gridApi.onFilterChanged(); // Reset Filter Options

      this.roleFilter = this.statusFilter = this.isVerifiedFilter = this.departmentFilter = {
        label: 'All',
        value: 'all'
      };
      this.$refs.filterCard.removeRefreshAnimation();
    },
    updateSearchQuery: function updateSearchQuery(val) {
      console.log(this.searchQuery); // this.gridApi.setQuickFilter(val)
    }
  },
  mounted: function mounted() {
    this.gridApi = this.gridOptions.api;
    this.getBoxSkins();
    this.getSkinType(); // console.log(this.totalProbability);
    // this.totalProbability = this.$parent.$parent.$parent.totalProbability

    /* =================================================================
      NOTE:
      Header is not aligned properly in RTL version of agGrid table.
      However, we given fix to this issue. If you want more robust solution please contact them at gitHub
    ================================================================= */

    if (this.$vs.rtl) {
      var header = this.$refs.agGridTable.$el.querySelector('.ag-header-container');
      header.style.left = "-".concat(String(Number(header.style.transform.slice(11, -3)) + 9), "px");
    }
  },
  created: function created() {
    if (!moduleUserManagement.isRegistered) {
      this.$store.registerModule('userManagement', moduleUserManagement);
      moduleUserManagement.isRegistered = true;
    }

    this.$store.dispatch('userManagement/fetchUsers').catch(function (err) {
      console.error(err);
    });
  }
};
