import { AgGridVue } from "ag-grid-vue";
import "@/assets/scss/vuexy/extraComponents/agGridStyleOverride.scss";
import vSelect from "vue-select"; // Store Module

import moduleUserManagement from "@/store/user-management/moduleUserManagement.js";
export default {
  components: {
    AgGridVue: AgGridVue,
    vSelect: vSelect
  },
  data: function data() {
    return {
      //表数据
      list: [{
        id: 1,
        name: "疯狂的蜗牛",
        box: "刀组",
        boxClass: "分类",
        open: "开出的饰品",
        boxspire: "12.66",
        ornament: "3.66",
        profit: "8.66",
        odds: "20%"
      }],
      // Filter Options
      roleFilter: {
        label: "正常玩家",
        value: "1"
      },
      roleOptions: [{
        label: "所有",
        value: "1"
      }, {
        label: "正常玩家",
        value: "2"
      }, {
        label: "机器人",
        value: "3"
      }],
      //开始时间
      input1: "2020-12-28",
      input2: "2020-12-29",
      searchQuery: "",
      // AgGrid
      gridApi: null,
      gridOptions: {},
      defaultColDef: {
        sortable: true,
        resizable: true,
        suppressMenu: true
      },
      columnDefs: [{
        headerName: "ID",
        field: "id",
        width: 125,
        filter: true
      }, {
        headerName: "玩家",
        field: "name",
        filter: true,
        width: 210
      }, {
        headerName: "消耗饰品",
        field: "box",
        filter: true,
        width: 225
      }, {
        headerName: "目标饰品",
        field: "boxClass",
        filter: true,
        width: 200
      }, {
        headerName: "饰品价值",
        field: "open",
        filter: true,
        width: 150
      }, {
        headerName: "成功/失败",
        field: "boxspire",
        filter: true,
        width: 150
      }, {
        headerName: "真几率",
        field: "ornament",
        filter: true,
        width: 150
      }, {
        headerName: "假几率",
        field: "profit",
        filter: true,
        width: 125
      }, {
        headerName: "时间",
        field: "odds",
        filter: true,
        width: 150
      }]
    };
  },
  watch: {
    roleFilter: function roleFilter(obj) {
      this.setColumnFilter("role", obj.value);
    },
    statusFilter: function statusFilter(obj) {
      this.setColumnFilter("status", obj.value);
    },
    isVerifiedFilter: function isVerifiedFilter(obj) {
      var val = obj.value === "all" ? "all" : obj.value === "yes" ? "true" : "false";
      this.setColumnFilter("is_verified", val);
    },
    departmentFilter: function departmentFilter(obj) {
      this.setColumnFilter("department", obj.value);
    }
  },
  computed: {
    usersData: function usersData() {
      //console.log(JSON.stringify(this.$store.state.userManagement.users) );
      return this.$store.state.userManagement.users;
    },
    paginationPageSize: function paginationPageSize() {
      if (this.gridApi) return this.gridApi.paginationGetPageSize();else return 10;
    },
    totalPages: function totalPages() {
      if (this.gridApi) return this.gridApi.paginationGetTotalPages();else return 0;
    },
    currentPage: {
      get: function get() {
        if (this.gridApi) return this.gridApi.paginationGetCurrentPage() + 1;else return 1;
      },
      set: function set(val) {
        this.gridApi.paginationGoToPage(val - 1);
      }
    }
  },
  methods: {
    //获取当天时间
    geiTime: function geiTime() {
      //今天的时间
      var day2 = new Date();
      day2.setTime(day2.getTime());
      var s2 = day2.getFullYear() + "-" + (day2.getMonth() + 1) + "-" + day2.getDate(); //明天的时间

      var day3 = new Date();
      day3.setTime(day3.getTime() + 24 * 60 * 60 * 1000);
      var s3 = day3.getFullYear() + "-" + (day3.getMonth() + 1) + "-" + day3.getDate();
      this.input1 = s2;
      this.input2 = s3;
    },
    setColumnFilter: function setColumnFilter(column, val) {
      var filter = this.gridApi.getFilterInstance(column);
      var modelObj = null;

      if (val !== "all") {
        modelObj = {
          type: "equals",
          filter: val
        };
      }

      filter.setModel(modelObj);
      this.gridApi.onFilterChanged();
    },
    resetColFilters: function resetColFilters() {
      // Reset Grid Filter
      this.gridApi.setFilterModel(null);
      this.gridApi.onFilterChanged(); // Reset Filter Options

      this.roleFilter = this.statusFilter = this.isVerifiedFilter = this.departmentFilter = {
        label: "All",
        value: "all"
      };
      this.$refs.filterCard.removeRefreshAnimation();
    },
    updateSearchQuery: function updateSearchQuery(val) {
      this.gridApi.setQuickFilter(val);
    }
  },
  mounted: function mounted() {
    this.geiTime();
    this.gridApi = this.gridOptions.api;
    /* =================================================================
      NOTE:
      Header is not aligned properly in RTL version of agGrid table.
      However, we given fix to this issue. If you want more robust solution please contact them at gitHub
    ================================================================= */

    if (this.$vs.rtl) {
      var header = this.$refs.agGridTable.$el.querySelector(".ag-header-container");
      header.style.left = "-".concat(String(Number(header.style.transform.slice(11, -3)) + 9), "px");
    }
  },
  created: function created() {
    if (!moduleUserManagement.isRegistered) {
      this.$store.registerModule("userManagement", moduleUserManagement);
      moduleUserManagement.isRegistered = true;
    }

    this.$store.dispatch("userManagement/fetchUsers").catch(function (err) {
      console.error(err);
    });
  }
};
