import "core-js/modules/es6.number.constructor";
import "core-js/modules/es6.regexp.replace";
import "core-js/modules/es6.regexp.to-string";
import "core-js/modules/es6.date.to-string";
import "core-js/modules/es6.function.name";
import "core-js/modules/es6.regexp.split";
import "core-js/modules/web.dom.iterable";
import "core-js/modules/es6.array.for-each";
import { AgGridVue } from "ag-grid-vue";
import "@/assets/scss/vuexy/extraComponents/agGridStyleOverride.scss";
import vSelect from "vue-select"; // Store Module

import moduleUserManagement from "@/store/user-management/moduleUserManagement.js";
import CellPlayer from "./cell/CellPlayer.vue";
import CellBox from "./cell/CellBox.vue";
export default {
  components: {
    AgGridVue: AgGridVue,
    vSelect: vSelect,
    CellPlayer: CellPlayer,
    CellBox: CellBox
  },
  data: function data() {
    return {
      //表数据
      list: [],
      statusOptions: [{
        label: '所有',
        value: ''
      }, {
        label: '已支付',
        value: '3'
      }, {
        label: '未支付',
        value: '1'
      }],
      //开始时间
      startTime: "0000-00-00 00:00:00",
      endTime: this.dateFormat('y-m-d h:i:s', new Date()),
      currentPage: 1,
      pageSize: 10,
      totalPages: 0,
      total: 0,
      status: '',
      // Filter Options
      roleFilter: {
        label: "所有",
        value: "1"
      },
      roleOptions: [{
        label: "所有",
        value: "1"
      }, {
        label: "未开始",
        value: "2"
      }, {
        label: "以结束",
        value: "3"
      }, {
        label: "以解散",
        value: "4"
      }],
      //开始时间
      input1: "2020-12-28",
      input2: "2020-12-29",
      searchQuery: "",
      // AgGrid
      gridApi: null,
      gridOptions: {},
      defaultColDef: {
        sortable: true,
        resizable: true,
        suppressMenu: true
      },
      columnDefs: [{
        headerName: "Id",
        field: "id",
        filter: true,
        width: 125
      }, {
        headerName: "时间",
        field: "create_time",
        filter: true,
        width: 190
      }, {
        headerName: "房主",
        field: "home_owner_name",
        filter: true,
        width: 135
      }, {
        headerName: "创建金额",
        field: "total_price",
        filter: true,
        width: 125
      }, {
        headerName: "开出饰品价值",
        field: "total_value",
        filter: true,
        width: 135
      }, {
        headerName: "获胜者",
        field: "winner_name",
        filter: true,
        width: 150
      }, {
        headerName: "胜利奖励",
        field: "winner_total",
        filter: true,
        width: 150
      }, {
        headerName: "参与人员",
        // field: "takePart",
        filter: true,
        // width: 220,
        cellRendererFramework: 'CellPlayer'
      }, {
        headerName: "加入的箱子",
        // field: "boxList",
        filter: true,
        // width: 220,
        cellRendererFramework: 'CellBox'
      }, // {
      //   headerName: "结算记录",
      //   field: "allot",
      //   filter: true,
      //   width: 150,
      // },
      {
        headerName: "状态",
        field: "statusStr",
        filter: true,
        width: 125
      }],
      components: {
        CellPlayer: CellPlayer,
        CellBox: CellBox
      }
    };
  },
  watch: {
    currentPage: function currentPage() {
      this.getList();
    },
    pageSize: function pageSize() {
      this.getList();
    }
  },
  computed: {},
  methods: {
    getList: function getList() {
      var _this = this;

      _this.$axios({
        url: '/admin/Record/battleRecord',
        method: "post",
        data: {
          page: _this.currentPage,
          pageSize: _this.pageSize,
          status: _this.status,
          searchKey: _this.searchQuery,
          startTime: _this.startTime,
          endTime: _this.endTime
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          var listData = res.data.data.list;
          listData.forEach(function (element) {
            element.total_price = element.total_price;

            if (element.winner == '' || element.winner == null) {
              element.winner_name = element.status == 3 || element.status == 2 ? '平局' : '';
              var total = 0;

              if (element.result_info) {
                element.result_info.forEach(function (el) {
                  el.forEach(function (e) {
                    total += parseFloat(e.price);
                  });
                });
                element.winner_total = (total / element.mode).toFixed(2) + ' * ' + element.mode;
              } else {
                element.winner_total = '0.00';
              }
            } else {
              var winners = element.winner.split(',');
              var winner_names = '';

              if (element.winner && element.player_info) {
                element.player_info.forEach(function (e) {
                  //1人获胜
                  if (winners.length == 1) {
                    if (element.winner == e.id) {
                      element.winner_name = e.name;
                    }
                  } //多人获胜


                  if (winners.length > 1) {
                    winners.forEach(function (winner_id, index) {
                      if (winner_id == e.id) {
                        console.log(e.name);

                        if (index == 0) {
                          winner_names = e.name;
                        } else {
                          winner_names += ',' + e.name;
                        }
                      }
                    });
                    element.winner_name = winner_names;
                  }
                });
              }

              var _total = 0;

              if (element.result_info) {
                element.result_info.forEach(function (el) {
                  el.forEach(function (e) {
                    _total += parseFloat(e.price);
                  });
                });
                element.winner_total = (_total / winners.length).toFixed(2) + (winners.length > 1 ? ' * ' + winners.length : '');
              }
            }
          });
          _this.list = listData;
          _this.total = res.data.data.total;
          _this.totalPages = _this.pageTotal(_this.total, _this.pageSize);
        } else {
          _this.list = [];
          _this.total = 0;
          _this.totalPages = 1;
        }
      });
    },
    search: function search() {
      this.status = this.roleFilter.value;
      this.currentPage = 1;
      this.getList();
    },
    clear: function clear() {
      if (this.startTime != '' || this.endTime != '' || this.status != '') {
        this.startTime = '';
        this.endTime = '';
        this.status = '';
        this.searchQuery = '';
        this.roleFilter = {
          label: '所有',
          value: ''
        };
        this.getList();
      }
    },
    //"总条数"：rowCount，"每页总条数"：pageSize
    pageTotal: function pageTotal(rowCount, pageSize) {
      if (rowCount == null || rowCount == "") {
        return 0;
      } else {
        if (pageSize != 0 && rowCount % pageSize == 0) {
          return parseInt(rowCount / pageSize);
        }

        if (pageSize != 0 && rowCount % pageSize != 0) {
          return parseInt(rowCount / pageSize) + 1;
        }
      }
    },
    dateFormat: function dateFormat(formatStr, fdate) {
      var fTime,
          fStr = 'ymdhis';
      if (!formatStr) formatStr = "y-m-d h:i:s";
      if (fdate) fTime = new Date(fdate);else fTime = new Date();
      var formatArr = [fTime.getFullYear().toString(), (fTime.getMonth() + 1).toString(), fTime.getDate().toString(), fTime.getHours().toString(), fTime.getMinutes().toString(), fTime.getSeconds().toString()];

      for (var i = 0; i < formatArr.length; i++) {
        formatStr = formatStr.replace(fStr.charAt(i), formatArr[i]);
      }

      return formatStr;
    },
    //获取当天时间
    geiTime: function geiTime() {
      //今天的时间
      var day2 = new Date();
      day2.setTime(day2.getTime());
      var s2 = day2.getFullYear() + "-" + (day2.getMonth() + 1) + "-" + day2.getDate(); //明天的时间

      var day3 = new Date();
      day3.setTime(day3.getTime() + 24 * 60 * 60 * 1000);
      var s3 = day3.getFullYear() + "-" + (day3.getMonth() + 1) + "-" + day3.getDate();
      this.input1 = s2;
      this.input2 = s3;
    },
    setColumnFilter: function setColumnFilter(column, val) {
      var filter = this.gridApi.getFilterInstance(column);
      var modelObj = null;

      if (val !== "all") {
        modelObj = {
          type: "equals",
          filter: val
        };
      }

      filter.setModel(modelObj);
      this.gridApi.onFilterChanged();
    },
    resetColFilters: function resetColFilters() {
      // Reset Grid Filter
      this.gridApi.setFilterModel(null);
      this.gridApi.onFilterChanged(); // Reset Filter Options

      this.roleFilter = this.statusFilter = this.isVerifiedFilter = this.departmentFilter = {
        label: "All",
        value: "all"
      };
      this.$refs.filterCard.removeRefreshAnimation();
    },
    updateSearchQuery: function updateSearchQuery(val) {
      this.gridApi.setQuickFilter(val);
    }
  },
  mounted: function mounted() {
    this.geiTime();
    this.gridApi = this.gridOptions.api;
    this.getList();
    /* =================================================================
      NOTE:
      Header is not aligned properly in RTL version of agGrid table.
      However, we given fix to this issue. If you want more robust solution please contact them at gitHub
    ================================================================= */

    if (this.$vs.rtl) {
      var header = this.$refs.agGridTable.$el.querySelector(".ag-header-container");
      header.style.left = "-".concat(String(Number(header.style.transform.slice(11, -3)) + 9), "px");
    }
  },
  created: function created() {
    if (!moduleUserManagement.isRegistered) {
      this.$store.registerModule("userManagement", moduleUserManagement);
      moduleUserManagement.isRegistered = true;
    }

    this.$store.dispatch("userManagement/fetchUsers").catch(function (err) {
      console.error(err);
    });
  }
};
