export default {
  name: 'CellRendererActions',
  data: function data() {
    return {
      delete_id: '',
      box_id: ''
    };
  },
  methods: {
    editRecord: function editRecord(params) {
      // this.$router.push(`/apps/user/user-edit/${  268}`).catch(() => {})
      this.$parent.$parent.editSkin(params);
      /*
              Below line will be for actual product
              Currently it's commented due to demo purpose - Above url is for demo purpose
                this.$router.push("/apps/user/user-edit/" + this.params.data.id).catch(() => {})
            */
    },
    confirmDeleteRecord: function confirmDeleteRecord(params) {
      console.log(params);
      this.delete_id = params.data.box_skins_id;
      this.box_id = params.data.box_id;
      this.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: '提示',
        text: "\u786E\u5B9A\u5220\u9664 \"".concat(this.params.data.itemName, "\"\uFF1F"),
        accept: this.deleteRecord,
        acceptText: '确定',
        cancelText: '取消'
      });
    },
    deleteRecord: function deleteRecord() {
      /* Below two lines are just for demo purpose */
      var _this = this;

      _this.$axios({
        url: "/admin/Box/delSkin",
        method: "post",
        data: {
          box_id: _this.box_id,
          id: _this.delete_id
        }
      }).then(function (res) {
        //  console.log(res);
        if (res.data.status == 1) {
          _this.$parent.$parent.getBoxSkins();

          _this.showDeleteSuccess();
        }
      });
      /* UnComment below lines for enabling true flow if deleting user */
      // this.$store.dispatch("userManagement/removeRecord", this.params.data.id)
      //   .then(()   => { this.showDeleteSuccess() })
      //   .catch(err => { console.error(err)       })

    },
    showDeleteSuccess: function showDeleteSuccess() {
      this.$vs.notify({
        color: 'success',
        title: '提示',
        text: '删除成功'
      });
    }
  }
};
