import { AgGridVue } from "ag-grid-vue";
import "@/assets/scss/vuexy/extraComponents/agGridStyleOverride.scss";
import vSelect from "vue-select";
import CellOperate from "./cell/CellOperate.vue";
import CellRendererLink from "./cell/CellRendererLink.vue"; // Store Module

import moduleUserManagement from "@/store/user-management/moduleUserManagement.js";
export default {
  components: {
    AgGridVue: AgGridVue,
    vSelect: vSelect,
    CellOperate: CellOperate,
    CellRendererLink: CellRendererLink
  },
  data: function data() {
    return {
      //表数据
      list: [],
      // Filter Options
      roleFilter: {
        label: "所有",
        value: "1"
      },
      roleOptions: [{
        label: "所有",
        value: "1"
      }, {
        label: "成功",
        value: "2"
      }, {
        label: "失败",
        value: "3"
      }],
      //开始时间
      startTime: "0000-00-00 00:00:00",
      endTime: this.dateFormat('y-m-d h:i:s', new Date()),
      currentPage: 1,
      pageSize: 10,
      totalPages: 0,
      total: 0,
      status: '',
      searchQuery: "",
      // AgGrid
      gridApi: null,
      gridOptions: {},
      defaultColDef: {
        sortable: true,
        resizable: true,
        suppressMenu: true
      },
      columnDefs: [{
        headerName: "ID",
        field: "id",
        width: 125,
        filter: true
      }, {
        headerName: "玩家",
        field: "uname",
        filter: true,
        width: 210,
        cellRendererFramework: 'CellRendererLink'
      }, {
        headerName: "名称",
        field: "name",
        filter: true,
        width: 300
      }, {
        headerName: "价格",
        field: "price",
        filter: true,
        width: 150
      }, {
        headerName: "状态",
        field: "statusStr",
        filter: true,
        width: 150
      }, {
        headerName: "获取方式",
        field: "wayStr",
        filter: true,
        width: 150
      }, {
        headerName: "系统订单号",
        field: "orderId",
        filter: true,
        width: 150
      }, {
        headerName: "第三方id",
        field: "open_number_md",
        filter: true,
        width: 150
      }, {
        headerName: '操作',
        field: 'transactions',
        width: 150,
        cellRendererFramework: 'CellOperate'
      }],
      components: {
        CellOperate: CellOperate
      }
    };
  },
  watch: {
    roleFilter: function roleFilter(obj) {
      this.setColumnFilter("role", obj.value);
    },
    statusFilter: function statusFilter(obj) {
      this.setColumnFilter("status", obj.value);
    },
    isVerifiedFilter: function isVerifiedFilter(obj) {
      var val = obj.value === "all" ? "all" : obj.value === "yes" ? "true" : "false";
      this.setColumnFilter("is_verified", val);
    },
    departmentFilter: function departmentFilter(obj) {
      this.setColumnFilter("department", obj.value);
    },
    currentPage: function currentPage() {
      this.getList();
    },
    pageSize: function pageSize() {
      this.getList();
    }
  },
  computed: {
    usersData: function usersData() {
      //console.log(JSON.stringify(this.$store.state.userManagement.users) );
      return this.$store.state.userManagement.users;
    },
    paginationPageSize: function paginationPageSize() {
      if (this.gridApi) return this.gridApi.paginationGetPageSize();else return 10;
    },
    totalPages: function totalPages() {
      if (this.gridApi) return this.gridApi.paginationGetTotalPages();else return 0;
    },
    currentPage: {
      get: function get() {
        if (this.gridApi) return this.gridApi.paginationGetCurrentPage() + 1;else return 1;
      },
      set: function set(val) {
        this.gridApi.paginationGoToPage(val - 1);
      }
    }
  },
  methods: {
    getList: function getList() {
      var _this = this;

      _this.$axios({
        url: '/admin/Record/skinToSteam',
        method: "post",
        data: {
          page: _this.currentPage,
          pageSize: _this.pageSize,
          status: _this.status,
          searchKey: _this.searchQuery,
          startTime: _this.startTime,
          endTime: _this.endTime
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          _this.list = res.data.data.list;
          _this.total = res.data.data.total;
          _this.totalPages = _this.pageTotal(_this.total, _this.pageSize);
        } else {
          _this.list = [];
          _this.total = 0;
          _this.totalPages = 1;
        }
      });
    },
    //"总条数"：rowCount，"每页总条数"：pageSize
    pageTotal: function pageTotal(rowCount, pageSize) {
      if (rowCount == null || rowCount == "") {
        return 0;
      } else {
        if (pageSize != 0 && rowCount % pageSize == 0) {
          return parseInt(rowCount / pageSize);
        }

        if (pageSize != 0 && rowCount % pageSize != 0) {
          return parseInt(rowCount / pageSize) + 1;
        }
      }
    },
    search: function search() {
      this.currentPage = 1;
      this.getList();
    },
    clear: function clear() {
      if (this.startTime != '' || this.endTime != '') {
        this.startTime = '0000-00-00 00:00:00';
        this.endTime = this.dateFormat('y-m-d h:i:s', new Date());
        this.searchQuery = '';
        this.roleFilter = {
          label: '所有',
          value: ''
        };
        this.getList();
      }
    },
    //获取当天时间
    geiTime: function geiTime() {
      //今天的时间
      var day2 = new Date();
      day2.setTime(day2.getTime());
      var s2 = day2.getFullYear() + "-" + (day2.getMonth() + 1) + "-" + day2.getDate(); //明天的时间

      var day3 = new Date();
      day3.setTime(day3.getTime() + 24 * 60 * 60 * 1000);
      var s3 = day3.getFullYear() + "-" + (day3.getMonth() + 1) + "-" + day3.getDate();
      this.input1 = s2;
      this.input2 = s3;
    },
    setColumnFilter: function setColumnFilter(column, val) {
      var filter = this.gridApi.getFilterInstance(column);
      var modelObj = null;

      if (val !== "all") {
        modelObj = {
          type: "equals",
          filter: val
        };
      }

      filter.setModel(modelObj);
      this.gridApi.onFilterChanged();
    },
    resetColFilters: function resetColFilters() {
      // Reset Grid Filter
      this.gridApi.setFilterModel(null);
      this.gridApi.onFilterChanged(); // Reset Filter Options

      this.roleFilter = this.statusFilter = this.isVerifiedFilter = this.departmentFilter = {
        label: "All",
        value: "all"
      };
      this.$refs.filterCard.removeRefreshAnimation();
    },
    updateSearchQuery: function updateSearchQuery(val) {
      this.gridApi.setQuickFilter(val);
    },
    dateFormat: function dateFormat(formatStr, fdate) {
      var fTime,
          fStr = 'ymdhis';
      if (!formatStr) formatStr = "y-m-d h:i:s";
      if (fdate) fTime = new Date(fdate);else fTime = new Date();
      var formatArr = [fTime.getFullYear().toString(), (fTime.getMonth() + 1).toString(), fTime.getDate().toString(), fTime.getHours().toString(), fTime.getMinutes().toString(), fTime.getSeconds().toString()];

      for (var i = 0; i < formatArr.length; i++) {
        formatStr = formatStr.replace(fStr.charAt(i), formatArr[i]);
      }

      return formatStr;
    }
  },
  mounted: function mounted() {
    this.getList();
  },
  created: function created() {
    if (!moduleUserManagement.isRegistered) {
      this.$store.registerModule("userManagement", moduleUserManagement);
      moduleUserManagement.isRegistered = true;
    }

    this.$store.dispatch("userManagement/fetchUsers").catch(function (err) {
      console.error(err);
    });
  }
};
