export default {
  name: "CellRendererActions",
  data: {
    return: function _return() {
      del_id: "";
    }
  },
  methods: {
    toEdit: function toEdit(row) {
      // console.log(this.$parent.$parent);
      this.$parent.$parent.editerData = row;
      console.log(this.$parent.$parent);
      this.$parent.$parent.sidebarActive = true;
    },
    editRecord: function editRecord() {
      this.$router.push("/apps/user/user-edit/".concat(268)).catch(function () {});
    },
    confirmDeleteRecord: function confirmDeleteRecord() {
      this.$vs.dialog({
        type: "confirm",
        color: "danger",
        title: "Confirm Delete",
        text: "You are about to delete \"".concat(this.params.data.username, "\""),
        accept: this.deleteRecord,
        acceptText: "Delete"
      });
    },
    del: function del(params) {
      var _this = this;

      _this.del_id = params.data.id;
      this.$vs.dialog({
        type: "confirm",
        title: "提示",
        text: "\u786E\u5B9A\u5220\u9664\uFF1F",
        accept: _this.delEnvelope,
        cancel: _this.cancel,
        acceptText: "确定",
        cancelText: "取消"
      });
    },
    delEnvelope: function delEnvelope() {
      console.log(1111);

      var _this = this;

      _this.$axios({
        url: "/admin/Activity/delEnvelope",
        method: "post",
        data: {
          id: _this.del_id
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          _this.notify(res.data.msg, "success");

          console.log(_this.$parent.$parent);

          _this.$parent.$parent.getEenvelopeList();
        } else {
          _this.notify(res.data.msg, "danger");
        }
      });
    },
    notify: function notify(msg, color) {
      var _this = this;

      _this.$vs.notify({
        title: "提示",
        text: msg,
        iconPack: "feather",
        icon: "icon-alert-circle",
        color: color
      });
    },
    deleteRecord: function deleteRecord() {
      this.showDeleteSuccess();
    },
    showDeleteSuccess: function showDeleteSuccess() {
      this.$vs.notify({
        color: "success",
        title: "User Deleted",
        text: "The selected user was successfully deleted"
      });
    }
  }
};
