import { AgGridVue } from 'ag-grid-vue';
import '@/assets/scss/vuexy/extraComponents/agGridStyleOverride.scss';
import vSelect from 'vue-select'; // Store Module

import moduleUserManagement from '@/store/user-management/moduleUserManagement.js'; // Cell Renderer

export default {
  components: {
    AgGridVue: AgGridVue,
    vSelect: vSelect
  },
  props: {
    data: {
      type: Object,
      required: true
    }
  },
  data: function data() {
    return {
      player_id: '',
      searchQuery: '',
      roleFilter: {
        label: '所有',
        value: ''
      },
      statusOptions: [{
        label: '所有',
        value: ''
      }, {
        label: '已支付',
        value: '3'
      }, {
        label: '未支付',
        value: '1'
      }],
      startTime: '0000-00-00 00:00:00',
      endTime: this.dateFormat('y-m-d h:i:s', new Date()),
      sum: '',
      sumSuccess: '',
      // AgGrid
      list: [],
      gridApi: null,
      gridOptions: {},
      defaultColDef: {
        sortable: true,
        resizable: true,
        suppressMenu: true
      },
      columnDefs: [{
        headerName: '用户',
        field: 'name',
        width: 150,
        filter: true
      }, {
        headerName: '手机号',
        field: 'mobile',
        width: 150,
        filter: true
      }, {
        headerName: '充值金额',
        field: 'money',
        width: 125,
        filter: true
      }, {
        headerName: '支付方式',
        field: 'mode',
        filter: true,
        width: 120
      }, {
        headerName: '订单号',
        field: 'order_num',
        filter: true,
        width: 240
      }, {
        headerName: '充值时间',
        field: 'create_time',
        filter: true,
        width: 200
      }, {
        headerName: '状态',
        field: 'statusStr',
        filter: true,
        width: 100
      }],
      // Cell Renderer Components
      components: {// CellRendererLink,
        // CellRendererStatus,
        // CellRendererVerified,
        // CellRendererActions
      },
      currentPage: 1,
      pageSize: 10,
      totalPages: 0,
      total: 0,
      status: ''
    };
  },
  watch: {
    roleFilter: function roleFilter(obj) {
      this.setColumnFilter('role', obj.value);
    },
    statusFilter: function statusFilter(obj) {
      this.setColumnFilter('status', obj.value);
    },
    isVerifiedFilter: function isVerifiedFilter(obj) {
      var val = obj.value === 'all' ? 'all' : obj.value === 'yes' ? 'true' : 'false';
      this.setColumnFilter('is_verified', val);
    },
    departmentFilter: function departmentFilter(obj) {
      this.setColumnFilter('department', obj.value);
    },
    pageSize: function pageSize() {
      this.getRechargeList();
    },
    currentPage: function currentPage() {
      this.getRechargeList();
    }
  },
  computed: {},
  methods: {
    getRechargeList: function getRechargeList() {
      var _this = this;

      if (_this.startTime && _this.endTime && _this.startTime > _this.endTime) {
        _this.$vs.notify({
          color: 'danger',
          title: '提示',
          text: '时间筛选有误'
        });

        return;
      }

      _this.$axios({
        url: '/admin/User/offlineRecharge',
        method: "post",
        data: {
          page: _this.currentPage,
          pageSize: _this.pageSize,
          player_id: _this.player_id,
          status: _this.status,
          startTime: _this.startTime,
          endTime: _this.endTime
        }
      }).then(function (res) {
        console.log(res);

        if (res.data.status == 1) {
          _this.list = res.data.data.list;
          _this.sum = res.data.data.sum;
          _this.sumSuccess = res.data.data.sumSuccess;
          _this.total = res.data.data.total;
          _this.totalPages = _this.pageTotal(_this.total, _this.pageSize);
        } else {
          _this.list = [];
          _this.total = 0;
          _this.totalPages = 1;
          _this.sum = res.data.data.sum;
          _this.sumSuccess = res.data.data.sumSuccess;
        }
      });
    },
    search: function search() {
      this.status = this.roleFilter.value;
      this.getRechargeList();
    },
    onSubmit: function onSubmit() {
      this.search();
    },
    clear: function clear() {
      if (this.startTime != '' || this.endTime != '' || this.status != '') {
        this.startTime = '';
        this.endTime = '';
        this.status = '';
        this.roleFilter = {
          label: '所有',
          value: ''
        };
        this.getRechargeList();
      }
    },
    //"总条数"：rowCount，"每页总条数"：pageSize
    pageTotal: function pageTotal(rowCount, pageSize) {
      if (rowCount == null || rowCount == "") {
        return 0;
      } else {
        if (pageSize != 0 && rowCount % pageSize == 0) {
          return parseInt(rowCount / pageSize);
        }

        if (pageSize != 0 && rowCount % pageSize != 0) {
          return parseInt(rowCount / pageSize) + 1;
        }
      }
    },
    setColumnFilter: function setColumnFilter(column, val) {
      // const filter = this.gridApi.getFilterInstance(column)
      // let modelObj = null
      // if (val !== 'all') {
      //   modelObj = { type: 'equals', filter: val }
      // }
      // filter.setModel(modelObj)
      // this.gridApi.onFilterChanged()
      this.status = val;
      this.getRechargeList();
    },
    resetColFilters: function resetColFilters() {
      // Reset Grid Filter
      this.gridApi.setFilterModel(null);
      this.gridApi.onFilterChanged(); // Reset Filter Options

      this.roleFilter = this.statusFilter = this.isVerifiedFilter = this.departmentFilter = {
        label: 'All',
        value: 'all'
      };
      this.$refs.filterCard.removeRefreshAnimation();
    },
    updateSearchQuery: function updateSearchQuery(val) {
      this.gridApi.setQuickFilter(val);
    },
    dateFormat: function dateFormat(formatStr, fdate) {
      var fTime,
          fStr = 'ymdhis';
      if (!formatStr) formatStr = "y-m-d h:i:s";
      if (fdate) fTime = new Date(fdate);else fTime = new Date();
      var formatArr = [fTime.getFullYear().toString(), (fTime.getMonth() + 1).toString(), fTime.getDate().toString(), fTime.getHours().toString(), fTime.getMinutes().toString(), fTime.getSeconds().toString()];

      for (var i = 0; i < formatArr.length; i++) {
        formatStr = formatStr.replace(fStr.charAt(i), formatArr[i]);
      }

      return formatStr;
    }
  },
  mounted: function mounted() {
    this.gridApi = this.gridOptions.api;
    this.player_id = this.data.id;
    this.getRechargeList();
    /* =================================================================
      NOTE:
      Header is not aligned properly in RTL version of agGrid table.
      However, we given fix to this issue. If you want more robust solution please contact them at gitHub
    ================================================================= */

    if (this.$vs.rtl) {
      var header = this.$refs.agGridTable.$el.querySelector('.ag-header-container');
      header.style.left = "-".concat(String(Number(header.style.transform.slice(11, -3)) + 9), "px");
    }
  },
  created: function created() {
    if (!moduleUserManagement.isRegistered) {
      this.$store.registerModule('userManagement', moduleUserManagement);
      moduleUserManagement.isRegistered = true;
    }

    this.$store.dispatch('userManagement/fetchUsers').catch(function (err) {
      console.error(err);
    });
  }
};
