import moduleUserManagement from "@/store/user-management/moduleUserManagement.js";
export default {
  components: {
    AgGridVue: AgGridVue,
    vSelect: vSelect
  },
  data: function data() {
    return {
      //表数据
      list: [],
      //开始时间
      startTime: "0000-00-00 00:00:00",
      endTime: this.dateFormat('y-m-d h:i:s', new Date()),
      searchQuery: "",
      roleFilter: {
        label: '所有',
        value: ''
      },
      statusOptions: [{
        label: '所有',
        value: ''
      }, {
        label: '已支付',
        value: '3'
      }, {
        label: '未支付',
        value: '1'
      }],
      // AgGrid
      gridApi: null,
      gridOptions: {},
      defaultColDef: {
        sortable: true,
        resizable: true,
        suppressMenu: true
      },
      columnDefs: [{
        headerName: "玩家",
        field: "name",
        filter: true,
        width: 150
      }, {
        headerName: "手机号",
        field: "mobile",
        filter: true,
        width: 150
      }, {
        headerName: "充值金额",
        field: "money",
        filter: true,
        width: 130
      }, {
        headerName: "支付方式",
        field: "mode",
        filter: true,
