export default {
  data: function data() {
    return {
      account: '',
      password: '',
      checked: false,
      adminInfo: []
    };
  },
  computed: {
    validateForm: function validateForm() {
      return !this.errors.any() && this.account !== '' && this.password !== '';
    }
  },
  methods: {
    checkLogin: function checkLogin() {
      // If user is already logged in notify
      if (this.$store.state.auth.isUserLoggedIn()) {
        // Close animation if passed as payload
        // this.$vs.loading.close()
        this.$vs.notify({
          title: 'Login Attempt',
          text: 'You are already logged in!',
          iconPack: 'feather',
          icon: 'icon-alert-circle',
          color: 'warning'
        });
        return false;
      }

      return true;
    },
    loginJWT: function loginJWT() {
      var _this = this;

      if (!_this.checkLogin()) return; // Loading

      _this.$vs.loading();

      var payload = {
        checked: _this.checked,
        userDetails: {
          account: _this.account,
          password: _this.password
        }
      };

      if (payload.checked) {
        _this.remember();
      }

      console.log(payload);

      _this.$axios({
        url: "/admin/Login/login",
        method: "post",
        data: payload.userDetails
      }).then(function (res) {
        _this.$vs.loading.close();

        if (res.data.status == 1) {
          _this.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'success'
          });

          setTimeout(function () {
            _this.$router.push({
              path: '/dashboard/ecommerce'
            });
          }, 500);
        } else {
          _this.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'danger'
          });
        }
      });
    },
    // 记住密码
    remember: function remember() {
      var _this = this;

      console.log(_this.checked);

      if (_this.checked) {
        _this.adminInfo = {
          account: _this.account,
          password: _this.password
        };
        console.log(_this.adminInfo);

        _this.setSessionStorage('rememberAmin', _this.adminInfo);
      } else {
        localStorage.removeItem('rememberAmin');
      }
    },
    setSessionStorage: function setSessionStorage(name, val) {
      return localStorage.setItem(name, JSON.stringify(val));
    },
    registerUser: function registerUser() {
      if (!this.checkLogin()) return;
      this.$router.push('/pages/register').catch(function () {});
    }
  },
  mounted: function mounted() {
    var _this = this;

    var rememberInfo = JSON.parse(localStorage.getItem("rememberAmin"));

    if (rememberInfo) {
      _this.checked = true;
      _this.account = rememberInfo.account;
      _this.password = rememberInfo.password;
    }
  }
};
