import "core-js/modules/es6.number.constructor";
//
//
//
export default {
  name: 'DelFrame',
  props: {
    delValide: {
      type: Boolean,
      required: true
    },
    delStatus: {
      type: [String, Number],
      default: null
    }
  },
  watch: {
    'delValide': {
      handler: function handler(val) {
        // console.log(val);
        if (val) {
          this.confirmDeleteRecord();
        }
      },
      deep: true
    },
    'delStatus': {
      handler: function handler(val) {
        // console.log(val);
        if (val == 1) {
          this.showDeleteSuccess();
        } else if (val == 2) {
          this.showDeleteDefault();
        }
      },
      deep: true
    }
  },
  data: function data() {
    return {
      delete_id: ''
    };
  },
  methods: {
    confirmDeleteRecord: function confirmDeleteRecord(params) {
      // this.delete_id = params.data.box_skins_id;
      this.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: '提示',
        text: "\u786E\u5B9A\u5220\u9664\uFF1F",
        accept: this.deleteRecord,
        cancel: this.cancel,
        acceptText: '确定',
        cancelText: '取消'
      });
    },
    deleteRecord: function deleteRecord() {
      var _this = this;

      _this.$emit('closeDel', true); //确认删除


      _this.$parent.delValide = false;
    },
    cancel: function cancel() {
      var _this = this;

      _this.$emit('closeDel', false); //不删除


      _this.$parent.delValide = false;
    },
    showDeleteSuccess: function showDeleteSuccess() {
      this.$vs.notify({
        color: 'success',
        title: '提示',
        text: '删除成功'
      });
    },
    showDeleteDefault: function showDeleteDefault() {
      this.$vs.notify({
        color: 'danger',
        title: '提示',
        text: '删除失败'
      });
    }
  },
  mounted: function mounted() {}
};
