export default {
  data: function data() {
    return {
      model: '',
      modelStatus: false,
      model_id: ''
    };
  },
  mounted: function mounted() {
    this.getModel();
  },
  watch: {
    modelStatus: function modelStatus(val) {
      if (val) {
        this.model = 'range';
      } else {
        this.model = 'stock';
      }
    }
  },
  methods: {
    getModel: function getModel() {
      var _this = this;

      _this.$axios({
        url: "/admin/Setting/getModel",
        method: "post",
        data: {}
      }).then(function (res) {
        if (res.data.status == 1) {
          _this.model = res.data.data.model;

          if (_this.model == 'stock') {
            _this.modelStatus = false;
          } else {
            _this.modelStatus = true;
          }

          _this.model_id = res.data.data.id;
        } else {
          _this.model = 'stock';
          _this.modelStatus = false;
        }
      });
    },
    change: function change() {
      var _this2 = this;

      var _this = this;

      _this.$axios({
        url: '/admin/Setting/editModel',
        method: "post",
        data: {
          id: _this.model_id,
          status: _this.modelStatus
        }
      }).then(function (res) {
        if (res.data.status == 1) {
          _this2.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'success'
          });
        } else {
          _this2.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'danger'
          });
        }

        _this.getModel();
      });
    }
  }
};
