import { AgGridVue } from 'ag-grid-vue';
import '@/assets/scss/vuexy/extraComponents/agGridStyleOverride.scss';
import vSelect from 'vue-select'; // Store Module

import moduleUserManagement from '@/store/user-management/moduleUserManagement.js'; // Cell Renderer

import CellRendererLink from "./cell-renderer/CellRendererLink.vue";
import CellRendererStatus from "./cell-renderer/CellRendererStatus.vue";
import CellRendererVerified from "./cell-renderer/CellRendererVerified.vue";
import CellRendererActions from "./cell-renderer/CellRendererActions.vue";
export default {
  components: {
    AgGridVue: AgGridVue,
    vSelect: vSelect,
    // Cell Renderer
    CellRendererLink: CellRendererLink,
    CellRendererStatus: CellRendererStatus,
    CellRendererVerified: CellRendererVerified,
    CellRendererActions: CellRendererActions
  },
  data: function data() {
    return {
      // Filter Options
      roleFilter: {
        label: '正常玩家',
        value: '1'
      },
      roleOptions: [{
        label: '所有',
        value: ''
      }, {
        label: '正常玩家',
        value: '1'
      }, {
        label: '机器人',
        value: '2'
      }],
      statusFilter: {
        label: '所有',
        value: ''
      },
      statusOptions: [{
        label: '所有',
        value: ''
      }, {
        label: '否',
        value: '0'
      }, {
        label: '是',
        value: '1'
      }],
      vipFilter: {
        label: '所有',
        value: ''
      },
      vipOptions: [{
        label: '所有',
        value: ''
      }, {
        label: '否',
        value: '0'
      }, {
        label: '是',
        value: '1'
      }],
      anchorFilter: {
        label: '所有',
        value: ''
      },
      anchorOptions: [{
        label: '所有',
        value: ''
      }, {
        label: '否',
        value: '0'
      }, {
        label: '是',
        value: '1'
      }],
      searchQuery: '',
      // AgGrid
      gridApi: null,
      gridOptions: {},
      defaultColDef: {
        sortable: true,
        resizable: true,
        suppressMenu: true
      },
      usersData: [],
      columnDefs: [// {
      //   headerName: 'ID',
      //   field: 'id',
      //   width: 125,
      //   filter: true,
      //   checkboxSelection: true,
      //   headerCheckboxSelectionFilteredOnly: true,
      //   headerCheckboxSelection: true
      // },
      {
        headerName: '昵称',
        field: 'name',
        filter: true,
        width: 180,
        cellRendererFramework: 'CellRendererLink'
      }, {
        headerName: '账号',
        field: 'mobile',
        filter: true,
        width: 150
      }, {
        headerName: '余额',
        field: 'total_amount',
        filter: true,
        width: 180
      }, {
        headerName: '状态',
        field: 'status',
        filter: true,
        width: 125
      }, {
        headerName: '允许取回',
        field: 'allow',
        filter: true,
        width: 125,
        cellRendererFramework: 'CellRendererStatus'
      }, {
        headerName: '概率组',
        field: 'group',
        filter: true,
        width: 100,
        cellRendererFramework: 'CellRendererStatus'
      }, {
        headerName: 'Vip',
        field: 'group_vip',
        filter: true,
        width: 100,
        cellRendererFramework: 'CellRendererStatus'
      }, {
        headerName: '主播',
        field: 'anchor',
        filter: true,
        width: 100,
        cellRendererFramework: 'CellRendererStatus'
      }, {
        headerName: '类型',
        field: 'type',
        filter: true,
        width: 125 // cellRendererFramework: 'CellRendererVerified',
        // cellClass: 'text-center'

      }, {
        headerName: '注册时间',
        field: 'create_time',
        filter: true,
        width: 180
      }, {
        headerName: '操作',
        // field: 'create_time',
        // filter: true,
        width: 120,
        cellRendererFramework: 'CellRendererActions',
        cellClass: 'text-center'
      }],
      // Cell Renderer Components
      components: {
        CellRendererLink: CellRendererLink,
        CellRendererStatus: CellRendererStatus,
        CellRendererVerified: CellRendererVerified,
        CellRendererActions: CellRendererActions
      },
      group: '',
      vip: '',
      anchor: '',
      total: 0,
      totalPages: 0,
      currentPage: 1,
      pageSize: 10
    };
  },
  watch: {
    "currentPage": {
      handler: function handler(val) {
        this.getUsersData();
      }
    },
    "pageSize": {
      handler: function handler(val) {
        this.getUsersData();
      }
    },
    roleFilter: function roleFilter(obj) {// this.setColumnFilter('role', obj.value)
    },
    statusFilter: function statusFilter(obj) {
      // this.setColumnFilter('status', obj.value)
      console.log(obj);
      this.group = obj.value;
      this.currentPage = 1;
      this.getUsersData();
    },
    vipFilter: function vipFilter(obj) {
      this.vip = obj.value;
      this.currentPage = 1;
      this.getUsersData();
    },
    anchorFilter: function anchorFilter(obj) {
      this.anchor = obj.value;
      this.currentPage = 1;
      this.getUsersData();
    },
    isVerifiedFilter: function isVerifiedFilter(obj) {
      var val = obj.value === 'all' ? 'all' : obj.value === 'yes' ? 'true' : 'false';
      this.setColumnFilter('is_verified', val);
    },
    departmentFilter: function departmentFilter(obj) {
      this.setColumnFilter('department', obj.value);
    }
  },
  computed: {
    // usersData () {
    //  // console.log(JSON.stringify(this.$store.state.userManagement.users) );
    //   return this.$store.state.userManagement.users
    // },
    paginationPageSize: function paginationPageSize() {
      if (this.gridApi) return this.gridApi.paginationGetPageSize();else return 10;
    } // totalPages () {
    //   if (this.gridApi) return this.gridApi.paginationGetTotalPages()
    //   else return 0
    // },
    // currentPage: {
    //   get () {
    //     if (this.gridApi) return this.gridApi.paginationGetCurrentPage() + 1
    //     else return 1
    //   },
    //   set (val) {
    //     this.gridApi.paginationGoToPage(val - 1)
    //   }
    // }

  },
  methods: {
    getUsersData: function getUsersData() {
      var _this = this; // usersData


      _this.$axios({
        url: "/admin/user/playerList",
        method: "post",
        data: {
          page: _this.currentPage,
          pageSize: _this.pageSize,
          group: _this.group,
          vip: _this.vip,
          anchor: _this.anchor,
          searchKey: _this.searchQuery
        }
      }).then(function (res) {
        // console.log(res);
        if (res.data.status == 1) {
          res.data.data.list.forEach(function (element) {
            // console.log(element);
            element['status'] = element['status'] == 1 ? '正常' : '冻结';
            element['type'] = element['type'] == 1 ? '正常玩家' : '非正常玩家'; // element['allow']  = element['allow']  == 1 ? '是' : '否';
          });
          _this.usersData = res.data.data.list;
          _this.total = res.data.data.total;
          _this.totalPages = _this.pageTotal(_this.total, _this.pageSize);
        } else {
          _this.usersData = [];
        }
      });
    },
    //"总条数"：rowCount，"每页总条数"：pageSize
    pageTotal: function pageTotal(rowCount, pageSize) {
      if (rowCount == null || rowCount == "") {
        return 0;
      } else {
        if (pageSize != 0 && rowCount % pageSize == 0) {
          return parseInt(rowCount / pageSize);
        }

        if (pageSize != 0 && rowCount % pageSize != 0) {
          return parseInt(rowCount / pageSize) + 1;
        }
      }
    },
    search: function search() {
      // this.status = this.roleFilter.value;
      this.currentPage = 1;
      this.getUsersData();
    },
    clear: function clear() {
      this.searchQuery = '';
      this.statusFilter = {
        label: '所有',
        value: ''
      }, this.vipFilter = {
        label: '所有',
        value: ''
      }, this.currentPage = 1;
      this.getUsersData();
    },
    setColumnFilter: function setColumnFilter(column, val) {
      var filter = this.gridApi.getFilterInstance(column);
      var modelObj = null;

      if (val !== 'all') {
        modelObj = {
          type: 'equals',
          filter: val
        };
      }

      filter.setModel(modelObj);
      this.gridApi.onFilterChanged();
    },
    resetColFilters: function resetColFilters() {
      console.log(this.statusFilter); // Reset Grid Filter

      this.gridApi.setFilterModel(null);
      this.gridApi.onFilterChanged(); // Reset Filter Options
      // this.roleFilter = this.statusFilter = this.isVerifiedFilter = this.departmentFilter = { label: '所有', value: '' }

      this.roleFilter = this.isVerifiedFilter = this.departmentFilter = this.statusFilter;
      this.$refs.filterCard.removeRefreshAnimation();
      this.getUsersData();
    },
    updateSearchQuery: function updateSearchQuery(val) {
      this.gridApi.setQuickFilter(val);
    }
  },
  mounted: function mounted() {
    this.getUsersData();
    this.gridApi = this.gridOptions.api;
    /* =================================================================
      NOTE:
      Header is not aligned properly in RTL version of agGrid table.
      However, we given fix to this issue. If you want more robust solution please contact them at gitHub
    ================================================================= */

    if (this.$vs.rtl) {
      var header = this.$refs.agGridTable.$el.querySelector('.ag-header-container');
      header.style.left = "-".concat(String(Number(header.style.transform.slice(11, -3)) + 9), "px");
    }
  },
  created: function created() {
    if (!moduleUserManagement.isRegistered) {
      this.$store.registerModule('userManagement', moduleUserManagement);
      moduleUserManagement.isRegistered = true;
    }

    this.$store.dispatch('userManagement/fetchUsers').catch(function (err) {
      console.error(err);
    });
  }
};
