            color: 'success'
          });

          _this2.$emit("closeSidebar");

          _this2.$emit("reload");

          _this2.initValues();
        } else {
          _this.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'danger'
          });
        }
      });
    }
