import VuePerfectScrollbar from "vue-perfect-scrollbar";
import { XIcon } from 'vue-feather-icons'

export default {
  props: {
    isSidebarActive: {
      type: Boolean,
      required: true,
    },
    data: {
      type: Object,
      default: () => {},
    },
  },
  components: {
    VuePerfectScrollbar,
    XIcon
  },
  data() {
    return {
      boxId: null,
      name: "",
      order: "",
      color:"",
      settings: {
        // perfectscrollbar settings
        maxScrollbarLength: 60,
        wheelSpeed: 0.6,
      },
      file:'',
      dataImg:'',
      form:[],
      status:''
    };
  },
  watch: {
    isSidebarActive(val) {
      if (!val) return;
      console.log(this.data);
      if(Object.entries(this.data).length === 0){
        this.initValues();
      }else{
        const { id, name,img,order,status} = JSON.parse(JSON.stringify(this.data));
        console.log(status);
        this.dataId = id;
        this.name = name;
        this.dataImg = img;
        this.order = order;
        this.status = status;
        this.getSubclass();
      }
    },
  },
  computed: {
    isSidebarActiveLocal: {
      get() {
        return this.isSidebarActive;
      },
      set(val) {
        if (!val) {
          this.$emit("closeSidebar");
          // this.$validator.reset()
          // this.initValues()
        }
      },
    },
    isFormValid() {
      return (
        !this.errors.any() &&
        this.name
      );
    },
    scrollbarTag() {
      return this.$store.getters.scrollbarTag;
    },
  },
  methods: {
    getSubclass(){
      let _this = this;
      _this.$axios({
          url: "/admin/Lucky/typeSubclass",
          method: "post",
          data: {
            skin_type_id:_this.dataId
          }
        }).then((res) => {
          console.log(res);
          if(res.data.status == 1){
            _this.form = res.data.data.list;
          }
        });
    },
    initValues() {
      if (this.data.id) return;
      this.dataId = null;
      this.name = "";
      this.order = '';
      this.status = '';
    },
    submitData() {
      let _this = this;
      var forms = new FormData()
      let data = {
        id:_this.dataId,
        name:_this.name,
        order:_this.order
      };
      let subclassInfo = _this.form ? _this.form : '';
      forms.append('data',JSON.stringify(data));
      forms.append('subclassInfo',JSON.stringify(subclassInfo));
      if(_this.file){
        forms.append('img',_this.file);
      }
      let url = "/admin/Lucky/addLucySkinType";
      if(this.dataId > 0){
        url = "/admin/Lucky/editLucySkinType";
      }
      _this.$axios({
          url: url,
          method: "post",
          data: forms
        }).then((res) => {
          if(res.data.status == 1){
            _this.file = '';
             _this.$vs.notify({
              title: '提示',
              text: res.data.msg,
              iconPack: 'feather',
              icon: 'icon-alert-circle',
              color: 'success'
            })
            this.$emit("closeSidebar");
            this.$emit("reload");
            this.initValues();
          }else{
            _this.$vs.notify({
              title: '提示',
              text: res.data.msg,
              iconPack: 'feather',
              icon: 'icon-alert-circle',
              color: 'danger'
            })
          }
        });
    },
     //新增规格输入框
    addItem(){
        let _this = this;
        let length = _this.form.length;
        let item = _this.form[length-1];
        // if(item>0){
            _this.form.push({
                type_id: _this.data.id,
                id: '',
                subclass_name: "",
            })
        // }else {
            // _this.form.dynamicItem.push({
            //     displayorder:1,
            //     goods_id: _this.goods_id,
            //     goods_option_id: '',
            //     id: '',
            //     thumb: "",
            //     title: "",
            // })
        // }
    },
    deleteItem (item, index) {
        let _this = this;
        _this.form.splice(index, 1)
    },
    updateCurrImg(input) {
      if (input.target.files && input.target.files[0]) {
        const reader = new FileReader();
        reader.onload = (e) => {
          this.dataImg = e.target.result;
        };
        reader.readAsDataURL(input.target.files[0]);
        this.file = input.target.files[0];
      }
    },
  }
};
