import DataViewSidebar from "./DataViewSidebar.vue";
import moduleDataList from "@/store/data-list/moduleDataList.js";

export default {
  components: {
    DataViewSidebar,
  },
  data() {
    return {
      selected: [],
      // products: [],
      itemsPerPage: 10,
      isMounted: false,
      addNewDataSidebar: false,
      sidebarData: {},
      data: [],
      url:'./room-component/NavMenu',
      free_room_id:'',

      currentPage:1,
      pageSize:10,
      totalPages:0,
      total:0
    };
  },
  computed: {
    // currentPage() {
    //   if (this.isMounted) {
    //     return this.$refs.table.currentx;
    //   }
    //   return 0;
    // },
    // products() {
    //   return this.$store.state.dataList.products;
    // },
    // queriedItems() {
    //   return this.$refs.table
    //     ? this.$refs.table.queriedResults.length
    //     : this.products.length;
    // },
  },
  watch:{
    'currentPage':{
      handler(val){
        // console.log(val);
        this.getRoomList();
      }
    },
    'queriedItems':{
      handler(val){
        // console.log(val);
      }
    }
  },
  methods: {
    getRoomList(){
      let _this = this;
      _this.$axios({
        url: "/admin/Free/freeRoomList",
        method: "post",
        data: {
          page:_this.currentPage,
          pageSize:_this.pageSize
        }
      }).then(res => {
        // console.log(res)
		if(res.data.status==1){
			_this.data = res.data.data.list;
			_this.total = res.data.data.total;
			_this.totalPages = _this.pageTotal(_this.total,_this.pageSize)
		}else{
			_this.data = []
			_this.total = 0
			_this.totalPages = 0
		}
        
      })
    },
      //"总条数"：rowCount，"每页总条数"：pageSize
    pageTotal(rowCount, pageSize) {
      if (rowCount == null || rowCount == "") {
            return 0;
      } else {
            if (pageSize != 0 && rowCount % pageSize == 0) {
                    return parseInt(rowCount / pageSize)
            }
            if (pageSize != 0 && rowCount % pageSize != 0) {
                    return parseInt(rowCount / pageSize) + 1;
            }
        }
    },
    del(id){
      let _this = this;
      _this.free_room_id = id;
      this.$vs.dialog({
        type: 'confirm',
        title: '提示',
        text: `确定删除？`,
        accept: _this.delRoom,
        cancel: _this.cancel,
        acceptText: '确定',
        cancelText:'取消'
      })
    },
    delRoom(){
      let _this = this;
      _this.$axios({
        url: "/admin/Free/delRoom",
        method: "post",
        data: {
          id:_this.free_room_id
        }
      }).then(res =>{
        if(res.data.status == 1){
          _this.notify(res.data.msg,'success');
          _this.getRoomList();
        }else{
          _this.notify(res.data.msg,'danger');
        }
      })
    },
    open(id){
      let _this = this;
      _this.free_room_id = id;
      this.$vs.dialog({
        type: 'confirm',
        // color: 'danger',
        title: '提示',
        text: `确定操作开奖？`,
        accept: _this.sure,
        cancel: _this.cancel,
        acceptText: '确定',
        cancelText:'取消'
      })
    },
    sure(id){
      let _this = this;
      _this.$axios({
        url: "/index/Free/getSkin",
        method: "post",
        data: {
          free_room_id:_this.free_room_id
        }
      }).then(res => {
        console.log(res);
        if(res.data.status == 1){
          _this.getRoomList();
           _this.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'success'
          })
        }else{
           _this.$vs.notify({
            title: '提示',
            text: res.data.msg,
            iconPack: 'feather',
            icon: 'icon-alert-circle',
            color: 'danger'
          })
        }
      })
    },
    notify(msg,color){
      let _this = this;
      _this.$vs.notify({
        title: '提示',
        text: msg,
        iconPack: 'feather',
        icon: 'icon-alert-circle',
        color: color
      })
    },
    addNewData() {
      this.sidebarData = {};
      this.toggleDataSidebar(true);
    },
    deleteData(id) {
      this.$store.dispatch("dataList/removeItem", id).catch((err) => {
        console.error(err);
      });
    },
    editNewData(data) {
      // this.sidebarData = JSON.parse(JSON.stringify(this.blankData))
      this.sidebarData = data;
      this.toggleDataSidebar(true);
    },
    detail(id){
      let _this = this;
      console.log(id);
      _this.$router.push({
        path:_this.url,
        query: {id:id}
      })
    },
    getOrderStatusColor(status) {
      if (status == "1") return "success";
      if (status == "2") return "danger";
      return "primary";
    },
    getPopularityColor(num) {
      if (num > 90) return "success";
      if (num > 70) return "primary";
      if (num >= 50) return "warning";
      if (num < 50) return "danger";
      return "primary";
    },
    toggleDataSidebar(val = false) {
      this.addNewDataSidebar = val;
    },
  },
  created() {
    if (!moduleDataList.isRegistered) {
      this.$store.registerModule("dataList", moduleDataList);
      moduleDataList.isRegistered = true;
    }
    this.$store.dispatch("dataList/fetchDataListItems");
  },
  mounted() {
    this.isMounted = true;
    this.getRoomList();
  },
};
