import VuePerfectScrollbar from 'vue-perfect-scrollbar'

export default {
  props: {
    isSidebarActive: {
      type: Boolean,
      required: true
    },
    data: {
      type: Object,
      default: () => {}
    }
  },
  components: {
    VuePerfectScrollbar
  },
  data () {
    return {
      name:'',
      type:'',
      desc:'',
      min:'',
      max:'',
      settings: { // perfectscrollbar settings
        maxScrollbarLength: 60,
        wheelSpeed: .60
      },
      taskType:[
        {'value':1,'label':'对战'},
        {'value':2,'label':'盲盒开箱'}
      ]
    }
  },
  watch: {
    isSidebarActive (val) {
      if (!val) return
      if (Object.entries(this.data).length === 0) {
        this.initValues()
        this.$validator.reset()
      } else {
        const { id, name ,type, min ,max } = JSON.parse(JSON.stringify(this.data))
        this.dataId = id
        this.name = name
        this.type = type
        this.min = min
        this.max = max
        this.initValues()
      }
      // Object.entries(this.data).length === 0 ? this.initValues() : { this.dataId, this.dataName, this.dataCategory, this.dataOrder_status, this.dataPrice } = JSON.parse(JSON.stringify(this.data))
    }
  },
  computed: {
    isSidebarActiveLocal: {
      get () {
        return this.isSidebarActive
      },
      set (val) {
        if (!val) {
          this.$emit('closeSidebar')
          // this.$validator.reset()
          // this.initValues()
        }
      }
    },
    isFormValid () {
      return !this.errors.any() && this.name && this.type && this.min && this.max> 0
    },
    scrollbarTag () { return this.$store.getters.scrollbarTag }
  },
  methods: {
    initValues () {
      if (this.data.id) return
      this.dataId = '';
        this.name = '';
        this.type = '';
        this.min = '';
        this.max = '';
    },
    submitData () {
      let _this = this;
      _this.$validator.validateAll().then(result => {
        if (result) {
          const params = {
            id: _this.dataId,
            name: _this.name,
            type: _this.type,
            min: _this.min,
            max: _this.max
          }
          _this.$axios({
            url: "/admin/Robot/addTask",
            method: "post",
            data:params
          }).then(res => {
            if(res.data.status == 1){
              _this.$emit('closeSidebar')
              _this.initValues()
              _this.notify('success','保存成功');
              // _this.task = res.data.data.list;
            }else{
              // _this.task = [];
              _this.notify('danger',res.data.msg);
            }
            
          })
        }
      })
    },
    notify(color,msg){
      this.$vs.notify({
        title: '提示',
        text: msg,
        iconPack: 'feather',
        icon: 'icon-alert-circle',
        // color: 'danger'
        color: color
      })
    },
    updateCurrImg (input) {
      if (input.target.files && input.target.files[0]) {
        const reader = new FileReader()
        reader.onload = e => {
          this.dataImg = e.target.result
        }
        reader.readAsDataURL(input.target.files[0])
      }
    }
  }
}
